            function compare(a, b) {
                if (a.Rank < 1 || b.Rank < 1 ) return b.Rank - a.Rank;
                else return a.Rank - b.Rank;         
            }
            vm.answers = vm.answers.sort(compare);
            
            getDisplayImages();

            $rootScope.canswers = vm.answers;
            //vm.answers = $filter('orderBy')(vm.answers, 'Rank');
