            //form match record
            var obj = {};
            obj.resource = [];


            var data = {};
            data.id = rec_id;
            data.vote = vote;
            data.timestmp = Date.now();

            obj.resource.push(data);

            var url = baseURI + '/_table/editvotetable'; 
            
            //update local record of votes
            var i = _editvotes.map(function (x) { return x.id; }).indexOf(rec_id);
